const initialState = {
    category: null,
    sortBy: 'popular'
}

const filters = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_CATEGORY':
          return {
              ...state,
              category:action.item
      }
    default:
      return state
  }
}

export default filters;
