import React from 'react';
import Categories from '../components/Categories';
import SortPopup from '../components/SortPopup';
import { Slider, Pizza } from '../components'
import pizzaDay from '../assets/img/pizza.jpg'
import { useSelector } from 'react-redux';

function Home({ popular }) {
  
  const { items } = useSelector(({ pizzas }) => {
    return {
      pizzas: state.pizzas.items,
      popular: state.popular.items
    }
  })

  return (
    <>
      <Slider />
      <div className="content">
        <div className="container">
          
        <div className="popular__pizzas">
          <img src={pizzaDay} alt="" />
          <hr />
          <h2 className="content__title">Популярные пиццы</h2>
          <div className="content__items">
            {popular.map((item) => {
              return <Pizza key={item.id} {...item}/>
            })}
          </div>
          </div>
          <hr />
          <div className="all__pizzas">
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__top">
              <Categories items={ ['Мясные','Вегетарианская','Гриль','Острые','Закрытые'] }/>
              <SortPopup items={ ['популярности','цене','алфавиту'] }/>
            </div>
            <div className="content__items">
              
              {items.map((item) => {
                return <Pizza key={item.id} {...item}/>
              })}
              
            </div>
          </div>
        </div>
        </div>
      </>
    );
}

export default Home;
