const initialState = {
  items: [],
  popular: [],
  isLoading:false
}

const pizzas = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_LOADING':
      return {
          ...state
      }
    case 'SET_PIZZAS':
      return {
          ...state,
          items: action.payload,
          isLoading:true
      }
    case 'SET_POP_PIZZAS':
      return {
          ...state,
          popular: action.payload,
          isLoading:true
      }
    default:
      return state
  }
}

export default pizzas;
