import React from 'react'
import './scss/app.scss'
import logoSvg from './assets/img/pizza-logo.png'
import {Header,Footer} from './components'
import {Home,Cart} from './page'
import { Route } from 'react-router-dom'
import { connect, useDispatch } from "react-redux";
import { setPizzas } from "./redux/actions/pizzas";
import { setPopularPizzas } from './redux/actions/popular'


function App() {
  const dispatch = useDispatch()

  React.useEffect(() => {
    fetch('http://localhost:3000/db.json')
      .then(response => response.json())
      .then(json => {
        dispatch(setPizzas(json.pizzas)),
        dispatch(setPopularPizzas(json.popular))
      })
  }, [])

  return (
    <>
    <div className="wrapper">
      <Header logoSvg={logoSvg} />
        <Route exact path={'/'} component={ Home }/>
        <Route exact path={'/cart'} component={Cart} />
      </div>
      <Footer />
    </>
  );
}

export default App;
