class App extends React.Component{

  componentDidMount() {
    fetch('http://localhost:3000/db.json')
      .then(response => response.json())
      .then(json => this.props.setPizzas(json.pizzas))
    fetch('http://localhost:3000/db.json')
      .then(response => response.json())
      .then(json =>this.props.setPopularPizzas(json.popularPizzas))
  }

  render() {
    return (
    <>
    <div className="wrapper">
      <Header logoSvg={logoSvg} />
          <Route exact path={'/'} render={() => <Home items={this.props.pizzas} popular={ popular}/>} />
        <Route exact path={'/cart'} component={Cart} />
      </div>
      <Footer />
    </>
  );
  }
}

const mapStateToProps = (state) => {
  return {
    pizzas: state.pizzas.items,
    popular: state.popular.items
  }
}

export default connect(mapStateToProps,{setPizzas,setPopularPizzas})(App);
