import React from 'react';
import { Slider, Pizza,PopularPizzas,SortPopup,Categories } from '../components'
import { useDispatch, useSelector } from 'react-redux';

const categories = ['Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']
const sort = ['популярности','цене','алфавиту'] 

function Home() {

  const dispatch = useDispatch()
  const {pizzas,popular} = useSelector((state) => {
    return {
      pizzas: state.pizzas.items,
      popular: state.popular.items
    }
  })

  const setCategory = (index) => {
    return dispatch(setCategory(index))
  }

  return (
    <>
      <Slider />
      <div className="content">
        <div className="container">
          <PopularPizzas popular={ popular }/>
        <hr />
          <div className="all__pizzas">
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__top">
              <Categories
                items={categories}
                onClickItem = {setCategory}
              />
              <SortPopup items={sort}/>
            </div>
            <div className="content__items">
              
              {pizzas.map((item) => {
                return <Pizza key={item.id} {...item}/>
              })}
              
            </div>
          </div>
        </div>
        </div>
      </>
    );
}

export default Home;
