import React from 'react';
import { Slider, Pizza,PopularPizzas,SortPopup,Categories,Loading } from '../components'
import { useDispatch, useSelector } from 'react-redux';
import { setCategory } from '../redux/actions/filters'
import { fechPizza,fechPopularPizza,setLoading } from "../redux/actions/pizzas";

const categories = ['Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']
const sort = ['популярности','цене','алфавиту'] 

function Home() {

  const dispatch = useDispatch()
  const {pizzas,popular,isLoading} = useSelector((state) => {
    return {
      pizzas: state.pizzas.items,
      popular: state.pizzas.popular,
      isLoading: state.pizzas.isLoading
    }
  })

  const onSelectCategory = React.useCallback((index) => {
    dispatch(setCategory(index))
  }, [])
  
  React.useEffect(() => {
    dispatch(fechPizza())
    dispatch(fechPopularPizza())
    dispatch(setLoading(false))
  }, [])
  

  return (
    <>
      <Slider />
      <div className="content">
        <div className="container">
          <PopularPizzas popular={ popular }/>
          <hr />
          <div className="all__pizzas">
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__top">
              <Categories
                items={categories}
                onClickItem = {onSelectCategory}
              />
              <SortPopup items={sort}/>
            </div>
            <div className="content__items">
              
              {!isLoading
                ? pizzas.map((item) => <Pizza key={item.id} {...item} />)
                : Array(8).fill(<Loading />)
              }
              
            </div>
          </div>
        </div>
        </div>
      </>
    );
}

export default Home;
