import React from 'react';
import { Slider, Pizza,PopularPizzas,SortPopup,Categories } from '../components'
import { useSelector } from 'react-redux';

function Home() {
  
  const {pizzas,popular} = useSelector((state) => {
    return {
      pizzas: state.pizzas.items,
      popular: state.popular.items
    }
  })


  return (
    <>
      <Slider />
      <div className="content">
        <div className="container">
          <PopularPizzas popular={ popular }/>
        <hr />
          <div className="all__pizzas">
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__top">
              <Categories items={ ['Мясные','Вегетарианская','Гриль','Острые','Закрытые'] }/>
              <SortPopup items={ ['популярности','цене','алфавиту'] }/>
            </div>
            <div className="content__items">
              
              {pizzas.map((item) => {
                return <Pizza key={item.id} {...item}/>
              })}
              
            </div>
          </div>
        </div>
        </div>
      </>
    );
}

export default Home;
